import React from 'react';

const GameWebsite = () => {
  // Add translations object at the top of the component
  const translations = {
    en: {
      welcome: "Welcome to AI Assistant",
      intro: "Step into the future of conversation. Our AI Assistant connects you with advanced, secure, and multilingual AI in a vibrant digital world.",
      enter: "Enter Site",
      features: "AI Features",
      featuresDesc: "Discover what makes our AI assistant unique in the cyber world.",
      featureList: [
        {
          title: "Natural Conversations",
          desc: "Engage in human-like, context-aware dialogue with advanced AI."
        },
        {
          title: "24/7 Availability",
          desc: "Get instant answers and support any time, day or night."
        },
        {
          title: "Secure & Private",
          desc: "Your conversations are encrypted and never shared."
        },
        {
          title: "Customizable",
          desc: "Personalize your AI assistant to fit your needs and style."
        },
        {
          title: "Multilingual",
          desc: "Communicate in multiple languages with seamless translation."
        },
      ],
      // ...add more UI strings as needed
    },
    hi: {
      welcome: "एआई सहायक में आपका स्वागत है",
      intro: "संवाद के भविष्य में कदम रखें। हमारा एआई सहायक आपको एक जीवंत डिजिटल दुनिया में उन्नत, सुरक्षित और बहुभाषी एआई से जोड़ता है।",
      enter: "साइट दर्ज करें",
      features: "एआई विशेषताएँ",
      featuresDesc: "जानें कि हमारा एआई सहायक साइबर दुनिया में कैसे अनूठा है।",
      featureList: [
        {
          title: "प्राकृतिक संवाद",
          desc: "एआई के साथ मानव-समान, संदर्भ-समझ संवाद करें।"
        },
        {
          title: "24/7 उपलब्धता",
          desc: "किसी भी समय, तुरंत उत्तर और सहायता प्राप्त करें।"
        },
        {
          title: "सुरक्षित और निजी",
          desc: "आपकी बातचीत एन्क्रिप्टेड है और कभी साझा नहीं की जाती।"
        },
        {
          title: "अनुकूलन योग्य",
          desc: "अपने एआई सहायक को अपनी आवश्यकताओं और शैली के अनुसार अनुकूलित करें।"
        },
        {
          title: "बहुभाषी",
          desc: "सीमलेस अनुवाद के साथ कई भाषाओं में संवाद करें।"
        },
      ],
      // ...add more UI strings as needed
    },
    es: {
      welcome: "Bienvenido a AI Assistant",
      intro: "Entra en el futuro de la conversación. Nuestro asistente de IA te conecta con una IA avanzada, segura y multilingüe en un mundo digital vibrante.",
      enter: "Entrar al sitio",
      features: "Características de IA",
      featuresDesc: "Descubre lo que hace único a nuestro asistente de IA en el mundo cibernético.",
      featureList: [
        {
          title: "Conversaciones Naturales",
          desc: "Interactúa en diálogos humanos y contextuales con IA avanzada."
        },
        {
          title: "Disponibilidad 24/7",
          desc: "Obtén respuestas y soporte instantáneo en cualquier momento."
        },
        {
          title: "Seguro y Privado",
          desc: "Tus conversaciones están encriptadas y nunca se comparten."
        },
        {
          title: "Personalizable",
          desc: "Personaliza tu asistente de IA según tus necesidades y estilo."
        },
        {
          title: "Multilingüe",
          desc: "Comunícate en varios idiomas con traducción fluida."
        },
      ],
      // ...add more UI strings as needed
    },
  };

  // State for selected language
  const [language, setLanguage] = React.useState('en');

  // Function to handle welcome modal
  const handleWelcome = () => {
    // This function would typically set a state to show/hide a welcome modal
    // For now, we'll just log the action
    console.log('Welcome modal closed');
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      {/* Hero Section - Cyber World Design */}
      <section className="relative h-screen flex items-center justify-center bg-gradient-to-br from-black via-gray-900 to-blue-950 overflow-hidden">
        {/* Animated Neon Grid */}
        <div className="absolute inset-0 z-0 pointer-events-none">
          <svg className="w-full h-full" viewBox="0 0 1440 900" fill="none" xmlns="http://www.w3.org/2000/svg">
            {/* Vertical grid lines */}
            {[...Array(15)].map((_, i) => (
              <line
                key={`v-${i}`}
                x1={(i * 96)}
                y1="0"
                x2={(i * 96)}
                y2="900"
                stroke="#00fff7"
                strokeWidth="1"
                opacity="0.08"
              />
            ))}
            {/* Horizontal grid lines */}
            {[...Array(10)].map((_, i) => (
              <line
                key={`h-${i}`}
                x1="0"
                y1={(i * 90)}
                x2="1440"
                y2={(i * 90)}
                stroke="#00fff7"
                strokeWidth="1"
                opacity="0.08"
              />
            ))}
            {/* Circuit/Network lines */}
            <polyline points="100,800 300,700 500,750 700,600 900,650 1200,500" fill="none" stroke="#ff00ea" strokeWidth="3" opacity="0.25" strokeDasharray="12 8"/>
            <polyline points="200,200 400,300 600,250 800,400 1000,350 1300,200" fill="none" stroke="#00fff7" strokeWidth="2" opacity="0.18" strokeDasharray="10 6"/>
          </svg>
          {/* Neon glow overlay */}
          <div className="absolute inset-0 bg-gradient-to-br from-cyan-400/10 via-fuchsia-500/10 to-blue-700/10 blur-2xl" />
        </div>
        <div className="text-center px-4 z-10">
          <h1 className="text-6xl md:text-8xl font-extrabold mb-6 tracking-widest animate-text bg-gradient-to-r from-cyan-400 via-fuchsia-500 to-blue-500 bg-clip-text text-transparent drop-shadow-[0_0_20px_#00fff7] font-mono uppercase">
            Enter the Cyber World
          </h1>
          <p className="text-2xl md:text-3xl mb-8 max-w-2xl mx-auto text-cyan-200 font-mono drop-shadow-[0_0_8px_#00fff7]">
            Where AI and humans connect in a neon digital universe.
          </p>
        </div>
        {/* Animated Neon Orbs */}
        <div className="absolute inset-0 pointer-events-none z-0">
          <div className="absolute top-1/4 left-1/4 w-32 h-32 rounded-full bg-cyan-400 opacity-30 blur-2xl animate-pulse"></div>
          <div className="absolute top-1/2 right-1/3 w-40 h-40 rounded-full bg-fuchsia-500 opacity-20 blur-3xl animate-ping"></div>
          <div className="absolute bottom-1/4 right-1/4 w-24 h-24 rounded-full bg-blue-400 opacity-30 blur-2xl animate-bounce"></div>
        </div>
        {/* Robot SVG Illustration */}
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 z-20">
          <style>{`
            @keyframes robot-bob {
              0%, 100% { transform: translateY(0); }
              50% { transform: translateY(-12px); }
            }
            @keyframes robot-blink {
              0%, 92%, 100% { transform: scaleY(1); }
              95% { transform: scaleY(0.1); }
              97% { transform: scaleY(1); }
            }
            @keyframes robot-antenna-pulse {
              0%, 100% { opacity: 0.7; r: 6; }
              50% { opacity: 1; r: 10; }
            }
          `}</style>
          <svg width="160" height="180" viewBox="0 0 160 180" fill="none" xmlns="http://www.w3.org/2000/svg"
            style={{ animation: 'robot-bob 3s ease-in-out infinite' }}>
            {/* Head */}
            <ellipse cx="80" cy="60" rx="45" ry="40" fill="#18181b" stroke="#00fff7" strokeWidth="4"/>
            {/* Eyes (with blink) */}
            <g style={{ transformOrigin: '65px 60px', animation: 'robot-blink 4s infinite' }}>
              <ellipse cx="65" cy="60" rx="7" ry="10" fill="#00fff7" opacity="0.85"/>
            </g>
            <g style={{ transformOrigin: '95px 60px', animation: 'robot-blink 4s infinite', animationDelay: '0.2s' }}>
              <ellipse cx="95" cy="60" rx="7" ry="10" fill="#00fff7" opacity="0.85"/>
            </g>
            {/* Mouth */}
            <rect x="70" y="80" width="20" height="6" rx="3" fill="#00fff7" opacity="0.5"/>
            {/* Antenna */}
            <rect x="77" y="18" width="6" height="20" rx="3" fill="#00fff7"/>
            <circle cx="80" cy="16" r="6" fill="#00fff7" opacity="0.7"
              style={{ transformOrigin: '80px 16px', animation: 'robot-antenna-pulse 1.5s infinite' }}
            />
            {/* Body */}
            <rect x="45" y="100" width="70" height="50" rx="18" fill="#18181b" stroke="#00fff7" strokeWidth="4"/>
            {/* Arms */}
            <rect x="20" y="110" width="20" height="10" rx="5" fill="#00fff7" opacity="0.3"/>
            <rect x="120" y="110" width="20" height="10" rx="5" fill="#00fff7" opacity="0.3"/>
            {/* Body lights */}
            <circle cx="80" cy="125" r="6" fill="#00fff7" opacity="0.7"/>
            <rect x="65" y="140" width="30" height="8" rx="4" fill="#00fff7" opacity="0.2"/>
          </svg>
        </div>
      </section>

      {/* AI Features Section */}
      <section id="features" className="py-20 px-4 max-w-7xl mx-auto">
        <h2 className="text-4xl font-bold mb-4 text-center bg-gradient-to-r from-cyan-400 via-fuchsia-500 to-blue-500 bg-clip-text text-transparent drop-shadow-[0_0_16px_#00fff7]">
          {translations[language].features}
        </h2>
        <p className="text-xl text-center text-gray-300 mb-14 max-w-2xl mx-auto">
          {translations[language].featuresDesc}
        </p>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-10">
          {translations[language].featureList.map((feature, idx) => {
            // Use the same icons as before, mapped by index
            const icons = [
              <svg className="w-10 h-10 text-cyan-400" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M12 4v16m8-8H4" /></svg>,
              <svg className="w-10 h-10 text-fuchsia-400" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><circle cx="12" cy="12" r="10" /><path strokeLinecap="round" strokeLinejoin="round" d="M8 12h4l2 2" /></svg>,
              <svg className="w-10 h-10 text-blue-400" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><rect x="3" y="11" width="18" height="10" rx="2" /><path strokeLinecap="round" strokeLinejoin="round" d="M7 11V7a5 5 0 0110 0v4" /></svg>,
              <svg className="w-10 h-10 text-green-400" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M12 8v4l3 3" /><circle cx="12" cy="12" r="10" /></svg>,
              <svg className="w-10 h-10 text-yellow-400" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M12 20v-6m0 0V4m0 10l-3-3m3 3l3-3" /></svg>,
            ];
            return (
              <div
                key={feature.title}
                className="relative group bg-white/10 backdrop-blur-xl rounded-3xl overflow-hidden border border-cyan-400/20 shadow-2xl transition-all duration-300 hover:scale-105 hover:shadow-[0_8px_40px_0_#00fff7cc] hover:border-fuchsia-400/40"
              >
                {/* Shine effect */}
                <div className="absolute inset-0 pointer-events-none opacity-0 group-hover:opacity-100 transition-opacity duration-500">
                  <div className="absolute -top-1/2 left-0 w-full h-full bg-gradient-to-br from-cyan-400/20 via-fuchsia-400/10 to-blue-400/20 blur-2xl animate-pulse" />
                </div>
                {/* Icon with glowing/gradient background */}
                <div className="flex items-center justify-center pt-10">
                  <div className="w-20 h-20 rounded-full flex items-center justify-center bg-gradient-to-br from-cyan-400 via-fuchsia-500 to-blue-500 shadow-[0_0_32px_0_#00fff7cc] group-hover:scale-110 transition-transform duration-300">
                    <div className="scale-125">{icons[idx]}</div>
                  </div>
                </div>
                <div className="p-8 text-center">
                  <h3 className="text-2xl font-bold mb-2 text-cyan-300 drop-shadow-[0_0_8px_#00fff7] group-hover:text-fuchsia-400 transition-colors duration-300">{feature.title}</h3>
                  <p className="text-gray-200 mb-2 font-medium group-hover:text-blue-200 transition-colors duration-300">{feature.desc}</p>
                </div>
                {/* Layered border/glow */}
                <div className="absolute inset-0 rounded-3xl border-2 border-transparent group-hover:border-fuchsia-400/40 pointer-events-none transition-all duration-300" />
              </div>
            );
          })}
        </div>
      </section>

      {/* Footer */}
      <footer className="bg-black py-8 text-center text-gray-400">
        <p>© {new Date().getFullYear()} Praveen. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default GameWebsite;
